import React from 'react';
import './CuisineCategories.css';

function CuisineCategories() {
    const cuisines = [
        { name: 'Italian', description: 'Savor the rich flavors of Italy with classics like pasta, pizza, and more, made with the freshest ingredients.' },
        { name: 'Chinese', description: 'Dive into the vibrant world of Chinese cuisine, offering everything from dim sum to stir-fries that delight the senses.' },
        { name: 'Mexican', description: 'Enjoy bold, zesty flavors with tacos, burritos, and other Mexican delights packed with spices and fresh ingredients.' },
        { name: 'Indian', description: 'Experience the rich, aromatic spices of India with curries, tandoori dishes, and flavorful street food favorites.' },
        { name: 'Japanese', description: 'Indulge in the delicacy of Japanese cuisine, featuring sushi, ramen, and other dishes that showcase balance and freshness.' },
        { name: 'Mediterranean', description: 'Relish the healthy and flavorful Mediterranean diet, with fresh seafood, hummus, falafel, and vibrant salads.' },
    ];

    return (
        <section className="cuisines">
            <h2>Explore International Cuisines</h2>
            <div className="cuisine-list">
                {cuisines.map((cuisine, index) => (
                    <div key={index} className="cuisine">
                        <h3>{cuisine.name}</h3>
                        <p>{cuisine.description}</p>
                    </div>
                ))}
            </div>
        </section>
    );
}

export default CuisineCategories;
