// src/components/Menu.js
import React from 'react';
import './Menu.css';

const Menu = () => {
  const foodItems = [
    { name: "Pizza", description: "Cheese, pepperoni, and tomato sauce", img: "/path/to/pizza.jpg" },
    { name: "Burger", description: "Juicy beef patty with fresh toppings", img: "/path/to/burger.jpg" },
    { name: "Pasta", description: "Classic Italian pasta with marinara sauce", img: "/path/to/pasta.jpg" },
  ];

  return (
    <section id="menu" className="menu">
      <h2>Our Menu</h2>
      <div className="food-items">
        {foodItems.map((item, index) => (
          <div key={index} className="food-item">
            <img src={item.img} alt={item.name} />
            <h3>{item.name}</h3>
            <p>{item.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Menu;
