    import React from 'react';
import './TopMeals.css';

function TopMeals() {
    const meals = [
        { name: 'Grilled Chicken', price: '$12.99', description: 'Freshly grilled chicken served on a bed of crisp greens with a light vinaigrette.' },
        { name: 'Classic Breakfast', price: '$9.99', description: 'Eggs, bacon, toast, and a side of fruit for the perfect start to your day' },
        // Add other meals here...
    ];

    return (
        <section className="top-meals">
            <h2>Our Top Meals</h2>
            <div className="meal-list">
                {meals.map((meal, index) => (
                    <div key={index} className="meal">
                        <h3>{meal.name}</h3>
                        <p>{meal.description}</p>
                        <span>{meal.price}</span>
                    </div>
                ))}
            </div>
        </section>
    );
}

export default TopMeals;
